package com.example.demo.pdd;

import com.pdd.pop.sdk.common.util.JsonUtil;
import com.pdd.pop.sdk.http.PopClient;
import com.pdd.pop.sdk.http.api.pop.request.PddGoodsImageUploadRequest;
import com.pdd.pop.sdk.http.api.pop.response.PddGoodsImageUploadResponse;

/**
 * @author liuxiaobin
 * @date 2020/6/28 10:52
 */
public class PddGoodsImageUploadDemo {

    public static void main(String[] args) throws Exception {
        PopClient client = PopClientBuilder.builder();

        /**
         * https://open.pinduoduo.com/application/document/api?id=pdd.goods.image.upload
         * pdd.goods.image.upload商品图片上传接口
         * 更新时间：2018-10-24 15:24:51
         * ¥免费
         * 必须用户授权
         * 商品图片上传服务（参数拼接成json串）
         */
        PddGoodsImageUploadRequest request = new PddGoodsImageUploadRequest();
        request.setImage("data:image/png;base64,");
        PddGoodsImageUploadResponse response = client.syncInvoke(request, PopClientBuilder.accessToken);
        System.out.println(JsonUtil.transferToJson(response));
        //{"goods_image_upload_response":{"image_url":"http://t00img.yangkeduo.com/openapi/images/2020-06-28/f0cafea648a9abe909de0a2de87e3b2b.png"}}
    }
}
